mapper.select:
		
		select bno,categoryname,title,content,writer,regdate from
  		( select /*+INDEX_DESC(teamPjt pk_board)*/ 
		rownum rn,bno,categoryname,title,content,writer,regdate
		from tbl_board 
 		where  
 		 

		
			rownum <= #{pageNum} * #{amount} 
  			)  
 			where rn > (#{pageNum}-1) * #{amount}
